CAT.IDL:
// Hash: 44d0ac66e039b9a459a4e2216958c6bdde12b4397d82f8235d2b2eddc969df07
// Version: 1.0.1
// -----------------------------------END_HEADER
include "../PLD/main.fbs";
include "../LCC/main.fbs";

// https://www.celestrak.com/satcat/satcat-format.php

enum objectType: byte {
  /// 0
  PAYLOAD,
  /// 1
  ROCKET_BODY,
  /// 2
  DEBRIS,
  /// 3
  UNKNOWN
}

enum opsStatusCode: byte {
  /// +
  OPERATIONAL,
  /// -
  NONOPERATIONAL,
  /// P
  PARTIALLY_OPERATIONAL,
  /// B
  BACKUP_STANDBY,
  /// S
  SPARE,
  /// X
  EXTENDED_MISSION,
  /// D
  DECAYED,
  /// ?
  UNKNOWN
}

enum dataStatusCode: byte {
  /// NCE
  NO_CURRENT_ELEMENTS,
  /// NIE
  NO_INITIAL_ELEMENTS,
  /// NEA
  NO_ELEMENTS_AVAILABLE,
  /// OK
  OK
}

enum orbitType: byte {
  /// 0
  ORBIT,
  /// 1
  LANDING,
  /// 2
  IMPACT,
  /// 3
  DOCKED,
  /// 4
  ROUNDTRIP
}

enum massType: byte {
  DRY,
  WET
}

/// Catalog Entity Message
table CAT {
  /// Satellite Name(s)
  OBJECT_NAME: string;
  /// International Designator (YYYY-NNNAAA)
  OBJECT_ID: string;
  /// NORAD Catalog Number
  NORAD_CAT_ID: uint;
  /// Object type (Payload, Rocket body, Debris, Unknown)
  OBJECT_TYPE: objectType = UNKNOWN;
  /// Operational Status Code
  OPS_STATUS_CODE: opsStatusCode = UNKNOWN;
  /// Ownership, typically country or company
  OWNER: legacyCountryCode;
  /// Launch Date [year-month-day] (ISO 8601)
  LAUNCH_DATE: string;
  /// Launch Site
  LAUNCH_SITE: string;
  /// Decay Date, if applicable [year-month-day] (ISO 8601)
  DECAY_DATE: string;
  /// Orbital period [minutes]
  PERIOD: double;
  /// Inclination [degrees]
  INCLINATION: double;
  /// Apogee Altitude [kilometers]
  APOGEE: double;
  /// Perigee Altitude [kilometers]
  PERIGEE: double;
  /// Radar Cross Section [meters2]; blank if no data available
  RCS: double;
  /// Data status code; blank otherwise
  DATA_STATUS_CODE: dataStatusCode;
  /// Orbit center
  ORBIT_CENTER: string;
  /// Orbit type (Orbit, Landing, Impact, Docked to RSO, roundtrip)
  ORBIT_TYPE: orbitType;
  /// Deployment Date [year-month-day] (ISO 8601)
  DEPLOYMENT_DATE: string;
  /// Indicates if the object is maneuverable
  MANEUVERABLE: bool;
  /// Size [meters]; blank if no data available
  SIZE: double;
  /// Mass [kilograms]; blank if no data available
  MASS: double;
  /// Mass type (Dry, Wet)
  MASS_TYPE: massType = DRY;
  /// Vector of PAYLOADS
  PAYLOADS: [PLD];
}

root_type CAT;
file_identifier "$CAT";

VCM.IDL:
// Hash: 8e522b102dc43b982b77cd5964a2973ca324bc51196c64ed5532cd6a5df8074b
// Version: 1.2.6
// -----------------------------------END_HEADER
include "../RFM/main.fbs";
include "../TIM/main.fbs";
include "../MET/main.fbs";

// Vector Covariance Message (VCM)
//
// The Vector Covariance Message (VCM) is used to exchange detailed orbit information, which
// includes the spacecraft state vector, Keplerian elements, equinoctial elements, and relevant
// physical parameters. The VCM provides a versatile format that can support a variety of
// propagators, models, and perturbations, allowing users to fully describe the orbital state
// and the conditions influencing its propagation.
//
// VCM supports both mean and osculating element sets, along with additional perturbative models
// like geopotential, atmospheric drag, solar radiation pressure, and resonance effects. Covariance
// matrices and custom user-defined fields can also be included to enhance precision and flexibility.
//
// The message recipient must use the appropriate orbit propagation algorithms and models to correctly
// propagate the orbit based on the provided data. The message is compatible with both classical
// and modern perturbation models, making it ideal for high-precision orbit prediction and analysis.
//
// CCSDS Reference documentation: 502.0-B-3.
// https://github.com/DigitalArsenal/spacedatastandards.org/blob/main/survey/legacy-messages/vcm/README.md

enum elementType : byte {
  OSCULATING = 0,
  MEAN = 1
}

enum anomalyType : byte {
  TRUE_ANOMALY = 0,
  MEAN_ANOMALY = 1
}

/// Enum to represent common atmospheric models
enum atmosphericModel : byte {
  NONE = 0,
  JACCHIA_70 = 1,
  JB2008 = 2,
  NRLMSISE_00 = 3,
  DTM_2000 = 4,
  HWM14 = 5,
  HASDM = 6
}

/// Enum to represent common geopotential models
enum geopotentialModel : byte {
  NONE = 0,
  EGM96 = 1,
  WGS84 = 2,
  JGM2 = 3,
  GEMT3 = 4,
  EGM96_J5 = 5
}

/// Enum to represent zonal harmonics
enum zonalHarmonic : byte {
  NONE = 0,
  J2 = 1,
  J3 = 2,
  J4 = 3,
  J5 = 4,
  J6 = 5,
  J7 = 6,
  J8 = 7,
  J9 = 8,
  J10 = 9,
  J11 = 10,
  J12 = 11
}

/// Enum to represent solar radiation pressure models
enum solarRadiationPressureModel : byte {
  NONE = 0,
  SPHERICAL_MODEL = 1,
  FLAT_PLATE_MODEL = 2
}

/// Enum to represent common lunar perturbation models
enum lunarPerturbationModel : byte {
  NONE = 0,
  DE430 = 1,
  DE431 = 2,
  LP150Q = 3
}

/// Enum to represent various solar perturbation models
enum solarPerturbationModel : byte {
  NONE = 0,
  DE430 = 1,
  DE431 = 2
}

/// Enum to represent resonance models
enum resonanceModel : byte {
  NONE = 0,
  HIGH_ALTITUDE_RESONANCE = 1,
  LOW_ALTITUDE_RESONANCE = 2,
  LUNAR_RESONANCE = 3,
  SOLAR_RESONANCE = 4
}

/// Enum to represent the status of various perturbations (ON/OFF)
enum perturbationStatus : byte {
  OFF = 0,
  ON = 1
}

/// Enum to represent propagator types
enum propagatorType : byte {
  NONE = 0,
  SEMI_ANALYTICAL = 1,
  VINTI = 2,
  SGP4 = 3,
  COWELL = 4,
  RK4 = 5,
  NYX = 6,
  GMAT = 7,
  SPICE = 8,
  SGP = 9,
  SDP4 = 10,
  SGP8 = 11,
  SDP8 = 12
}

/// Propagator configuration structure to describe propagation settings
table propagatorConfig {
  PROPAGATOR_NAME: string;
  PROPAGATOR_TYPE: propagatorType;
  FORCE_MODELS: [string];
  EPOCH: string;
  TIME_STEP: double;
  ZONAL_HARMONIC_TERMS: [zonalHarmonic];  // Full list of zonal harmonic terms (J2 through J12)
}

/// VCM State Vector (position and velocity)
table VCMStateVector {
  EPOCH: string;
  X: double;
  Y: double;
  Z: double;
  X_DOT: double;
  Y_DOT: double;
  Z_DOT: double;
}

/// VCM Covariance Matrix Line
table VCMCovarianceMatrixLine {
  CX_X: double;
  CY_X: double;
  CZ_X: double;
  CX_DOT_X: double;
}

/// Keplerian Elements
table keplerianElements {
  SEMI_MAJOR_AXIS: double; // km
  ECCENTRICITY: double;
  INCLINATION: double;     // degrees
  RA_OF_ASC_NODE: double;  // degrees
  ARG_OF_PERICENTER: double; // degrees
  ANOMALY_TYPE: anomalyType;
  ANOMALY: double;         // degrees
}

/// Equinoctial Elements
table equinoctialElements {
  AF: double;           // e cos(ω + frΩ)
  AG: double;           // e sin(ω + frΩ)
  L: double;            // True longitude
  N: double;            // Semi-major axis (a)
  CHI: double;          // TAN(i/2) SIN(Ω)
  PSI: double;          // TAN(i/2) COS(Ω)
}

/// UVW Sigmas (Covariance matrix in UVW frame)
table uvwSigmas {
  U_SIGMA: double;        // Standard deviation in U direction
  V_SIGMA: double;        // Standard deviation in V direction
  W_SIGMA: double;        // Standard deviation in W direction
  UD_SIGMA: double;       // Standard deviation in U direction for velocity
  VD_SIGMA: double;       // Standard deviation in V direction for velocity
  WD_SIGMA: double;       // Standard deviation in W direction for velocity
}

/// VCM Atmospheric and Perturbation Model Data
table VCMAtmosphericModelData {
  ATMOSPHERIC_MODEL: atmosphericModel;
  GEOPOTENTIAL_MODEL: geopotentialModel;
  LUNAR_SOLAR_PERTURBATION: perturbationStatus;
  LUNAR_PERTURBATION_MODEL: lunarPerturbationModel;
  SOLAR_PERTURBATION_MODEL: solarPerturbationModel;
  SOLAR_RADIATION_PRESSURE: perturbationStatus;
  SRP_MODEL: solarRadiationPressureModel;
  RESONANCE_MODEL: resonanceModel;
}

/// Vector Covariance Message
table VCM {
  CCSDS_OMM_VERS: double;
  CREATION_DATE: string;
  ORIGINATOR: string;

  OBJECT_NAME: string;
  OBJECT_ID: string;      // International Designator (YYYY-NNNAAA)
  CENTER_NAME: string;
  REF_FRAME: string;
  TIME_SYSTEM: string;

  STATE_VECTOR: VCMStateVector;
  KEPLERIAN_ELEMENTS: keplerianElements;
  EQUINOCTIAL_ELEMENTS: equinoctialElements;

  GM: double;              // Gravitational Coefficient

  ATMOSPHERIC_MODEL_DATA: VCMAtmosphericModelData;
  PROPAGATOR_SETTINGS: propagatorConfig;

  COVARIANCE_MATRIX: [VCMCovarianceMatrixLine];
  UVW_SIGMAS: uvwSigmas;

  // Spacecraft Parameters from OMM and OCM
  MASS: double;
  SOLAR_RAD_AREA: double;
  SOLAR_RAD_COEFF: double; // Solar Radiation Pressure Coefficient
  DRAG_AREA: double;
  DRAG_COEFF: double;
  SRP: perturbationStatus; // Solar Radiation Pressure

  // TLE Related Parameters
  CLASSIFICATION_TYPE: string;
  NORAD_CAT_ID: uint32;
  ELEMENT_SET_NO: uint32;
  REV_AT_EPOCH: double;
  BSTAR: double;
  MEAN_MOTION_DOT: double;
  MEAN_MOTION_DDOT: double;

  // Covariance Matrix for position/velocity
  COV_REFERENCE_FRAME: string;
  CX_X: double;
  CY_X: double;
  CZ_X: double;
  CX_DOT_X: double;

  // User-defined fields
  USER_DEFINED_BIP_0044_TYPE: uint;
  USER_DEFINED_OBJECT_DESIGNATOR: string;
  USER_DEFINED_EARTH_MODEL: string;
  USER_DEFINED_EPOCH_TIMESTAMP: double;
  USER_DEFINED_MICROSECONDS: double;
}

root_type VCM;

CDM.IDL:
// Hash: a1ea2fb507304e144950ef981c89a09b1ab82d0d7c19147c948b8a666b74d4d7
// Version: 1.0.2
// -----------------------------------END_HEADER
include "../PNM/main.fbs";
include "../CAT/main.fbs";
include "../EPM/main.fbs";
include "../RFM/main.fbs";

enum screeningVolumeShape : byte {
  ELLIPSOID,
  BOX
}

enum covarianceMethod : byte {
  CALCULATED,
  DEFAULT
}

table CDMObject {
  /// A comment
  COMMENT:string;
  // Catalog Definition
  OBJECT:CAT;
  /// Point of Contact
  POC:EPM;
  /// Operator contact position
  OPERATOR_CONTACT_POSITION:string;
  /// Operator organization
  OPERATOR_ORGANIZATION:string;
  /// Ephemeris name
  EPHEMERIS_NAME:string;
  /// Covariance method
  COVARIANCE_METHOD:covarianceMethod;
  /// Reference Frame in which the object position is defined
  REFERENCE_FRAME:RFM;
  /// Gravity model
  GRAVITY_MODEL:string;
  /// Atmospheric model
  ATMOSPHERIC_MODEL:string;
  /// N-body perturbations
  N_BODY_PERTURBATIONS:string;
  /// Solar radiation pressure
  SOLAR_RAD_PRESSURE:bool;
  /// Earth tides
  EARTH_TIDES:bool;
  /// Intrack thrust
  INTRACK_THRUST:bool;
  /// Time of last observation start
  TIME_LASTOB_START:string;
  /// Time of last observation end
  TIME_LASTOB_END:string;
  /// Recommended observation data span
  RECOMMENDED_OD_SPAN:double;
  /// Actual observation data span
  ACTUAL_OD_SPAN:double;
  /// Number of observations available
  OBS_AVAILABLE:uint;
  /// Number of observations used
  OBS_USED:uint;
  /// Number of tracks available
  TRACKS_AVAILABLE:uint;
  /// Number of tracks used
  TRACKS_USED:uint;
  /// Residuals accepted
  RESIDUALS_ACCEPTED:double;
  /// Weighted root mean square
  WEIGHTED_RMS:double;
  /// Area of the object
  AREA_PC:double;
  /// Area of the object drag
  AREA_DRG:double;
  /// Area of the object solar radiation pressure
  AREA_SRP:double;
  /// Object's area-to-mass ratio
  CR_AREA_OVER_MASS:double;
  /// Object's thrust acceleration
  THRUST_ACCELERATION:double;
  /// Object's solar flux
  SEDR:double;
  /// X-coordinate of the object's position in RTN coordinates
  X:double;
  /// Y-coordinate of the object's position in RTN
  Y:double;
  /// Z-coordinate of the object's position in RTN
  Z:double;
  /// X-coordinate of the object's position in RTN coordinates
  X_DOT:double;
  /// Y-coordinate of the object's position in RTN
  Y_DOT:double;
  /// Z-coordinate of the object's position in RTN
  Z_DOT:double;

  // (Covariance Matrix 9×9 Lower Triangular Form. All parameters of the 6×6 position/velocity submatrix must be given)

  /// Covariance Matrix component
  CR_R:double;
  /// Covariance Matrix component
  CT_R:double;
  /// Covariance Matrix component
  CT_T:double;
  /// Covariance Matrix component
  CN_R:double;
  /// Covariance Matrix component
  CN_T:double;
  /// Covariance Matrix component
  CN_N:double;
  /// Covariance Matrix component
  CRDOT_R:double;
  /// Covariance Matrix component
  CRDOT_T:double;
  /// Covariance Matrix component
  CRDOT_N:double;
  /// Covariance Matrix component
  CRDOT_RDOT:double;
  /// Covariance Matrix component
  CTDOT_R:double;
  /// Covariance Matrix component
  CTDOT_T:double;
  /// Covariance Matrix component
  CTDOT_N:double;
  /// Covariance Matrix component
  CTDOT_RDOT:double;
  /// Covariance Matrix component
  CTDOT_TDOT:double;
  /// Covariance Matrix component
  CNDOT_R:double;
  /// Covariance Matrix component
  CNDOT_T:double;
  /// Covariance Matrix component
  CNDOT_N:double;
  /// Covariance Matrix component
  CNDOT_RDOT:double;
  /// Covariance Matrix component
  CNDOT_TDOT:double;
  /// Covariance Matrix component
  CNDOT_NDOT:double;
  /// Covariance Matrix component
  CDRG_R:double;
  /// Covariance Matrix component
  CDRG_T:double;
  /// Covariance Matrix component
  CDRG_N:double;
  /// Covariance Matrix component
  CDRG_RDOT:double;
  /// Covariance Matrix component
  CDRG_TDOT:double;
  /// Covariance Matrix component
  CDRG_NDOT:double;
  /// Covariance Matrix component
  CDRG_DRG:double;
  /// Covariance Matrix component
  CSRP_R:double;
  /// Covariance Matrix component
  CSRP_T:double;
  /// Covariance Matrix component
  CSRP_N:double;
  /// Covariance Matrix component
  CSRP_RDOT:double;
  /// Covariance Matrix component
  CSRP_TDOT:double;
  /// Covariance Matrix component
  CSRP_NDOT:double;
  /// Covariance Matrix component
  CSRP_DRG:double;
  /// Covariance Matrix component
  CSRP_SRP:double;
  /// Covariance Matrix component
  CTHR_R:double;
  /// Covariance Matrix component
  CTHR_T:double;
  /// Covariance Matrix component
  CTHR_N:double;
  /// Covariance Matrix component
  CTHR_RDOT:double;
  /// Covariance Matrix component
  CTHR_TDOT:double;
  /// Covariance Matrix component
  CTHR_NDOT:double;
  /// Covariance Matrix component
  CTHR_DRG:double;
  /// Covariance Matrix component
  CTHR_SRP:double;
  /// Covariance Matrix component
  CTHR_THR:double;
}

/// Conjunction Data Message
table CDM {
  /// The version of the CCSDS CDM standard used
  CCSDS_CDM_VERS:double;
  /// The date the CDM message was created
  CREATION_DATE:string;
  /// The originator of the CDM message
  ORIGINATOR:string;
  /// The intended recipient of the CDM message
  MESSAGE_FOR:string;
  /// A unique identifier for the CDM message
  MESSAGE_ID:string;
  /// Time of closest approach
  TCA:string;
  /// The miss distance between the two objects
  MISS_DISTANCE:double;
  /// The relative speed between the two objects
  RELATIVE_SPEED:double;
  /// The relative position R component
  RELATIVE_POSITION_R:double;
  /// The relative position T component
  RELATIVE_POSITION_T:double;
  /// The relative position N component
  RELATIVE_POSITION_N:double;
  /// The relative velocity R component
  RELATIVE_VELOCITY_R:double;
  /// The relative velocity T component
  RELATIVE_VELOCITY_T:double;
  /// The relative velocity N component
  RELATIVE_VELOCITY_N:double;
  /// The start time of the screening period
  START_SCREEN_PERIOD:string;
  /// The end time of the screening period
  STOP_SCREEN_PERIOD:string;
  /// The reference frame for the screening volume
  SCREEN_VOLUME_FRAME:RFM;
  /// The shape of the screening volume
  SCREEN_VOLUME_SHAPE:screeningVolumeShape;
  /// The X dimension of the screening volume
  SCREEN_VOLUME_X:double;
  /// The Y dimension of the screening volume
  SCREEN_VOLUME_Y:double;
  /// The Z dimension of the screening volume
  SCREEN_VOLUME_Z:double;
  /// The time the objects entered the screening volume
  SCREEN_ENTRY_TIME:string;
  /// The time the objects exited the screening volume
  SCREEN_EXIT_TIME:string;
  /// The probability of collision between the two objects
  COLLISION_PROBABILITY:double;
  /// The method used to calculate the collision probability
  COLLISION_PROBABILITY_METHOD:string;
  /// The first object in the CDM message
  OBJECT1:CDMObject;
  /// The second object in the CDM message
  OBJECT2:CDMObject;
  /// Data Source for the positional information for Object 1
  OBJECT1_DATASOURCE:PNM;
  /// Data Source for the positional information for Object 2
  OBJECT2_DATASOURCE:PNM;
}


root_type CDM;
file_identifier "$CDM";

IDM.IDL:
// Hash: 9142dced239a49aa8bf81bad7f2dcb57688340389780e43684fd5eaa1f7a82ab
// Version: 1.0.3
// -----------------------------------END_HEADER
/// Different types of polarization in EMT
enum PolarizationType: byte {
  linear,
  circular,
  elliptical,
  unpolarized
}

/// Simple polarization types
enum SimplePolarization: byte {
  vertical,
  horizontal,
  leftHandCircular,
  rightHandCircular
}

/// Frequency range with lower and upper limits
table FrequencyRange {
  /// Lower frequency in MHz
  LOWER: double;
  /// Upper frequency in MHz
  UPPER: double;
}

/// Stokes parameters, representing different aspects of polarization
table StokesParameters {
  /// Intensity
  I: double;
  /// Linear polarization
  Q: double;
  /// Another linear polarization, orthogonal to Q
  U: double;
  /// Circular polarization
  V: double;
}

/// Table representing a frequency band with a name and frequency range
table Band {
  /// Name of the band
  NAME: string;
  /// Frequency range of the band
  FREQUENCY_RANGE: FrequencyRange;
}

/// Enum for the mode of data (real, simulated, synthetic)
enum DataMode: byte {
  /// Data collected during an exercise scenario.
  EXERCISE,
  /// Data collected from real-world observations.
  REAL,
  /// Data generated through simulation.
  SIMULATED,
  /// Data collected for testing purposes.
  TEST
}

enum DeviceType: byte {

  // Basic and General Sensors

  /// Basic or undefined sensor type
  UNKNOWN,
  /// General optical sensors
  OPTICAL,

  // Electromagnetic Spectrum Sensors

  /// Detects infrared radiation
  INFRARED_SENSOR,
  /// Sensitive to ultraviolet light
  ULTRAVIOLET_SENSOR,
  /// For X-ray detection
  X_RAY_SENSOR,
  /// For gamma-ray detection
  GAMMA_RAY_SENSOR,

  // Radar and Radio Telescopes

  /// Basic radar systems
  RADAR,
  /// Advanced radar with phased array technology
  PHASED_ARRAY_RADAR,
  /// For high-resolution imaging
  SYNTHETIC_APERTURE_RADAR,
  /// For astronomical observations using bistatic setup
  BISTATIC_RADIO_TELESCOPE,
  /// For radio astronomy
  RADIO_TELESCOPE,

  // Environmental and Atmospheric Sensors

  /// For atmospheric studies
  ATMOSPHERIC_SENSOR,
  /// For observing space weather phenomena
  SPACE_WEATHER_SENSOR,
  /// General environmental monitoring
  ENVIRONMENTAL_SENSOR,

  // Specialized Scientific Sensors

  /// For measuring seismic activities
  SEISMIC_SENSOR,
  /// For gravity measurements
  GRAVIMETRIC_SENSOR,
  /// For magnetic field detection
  MAGNETIC_SENSOR,
  /// For electromagnetic field analysis
  ELECTROMAGNETIC_SENSOR,
  /// For temperature and heat detection
  THERMAL_SENSOR,
  /// For detecting chemicals and substances
  CHEMICAL_SENSOR,
  /// For biological research and detection
  BIOLOGICAL_SENSOR,
  /// For detecting ionizing radiation
  RADIATION_SENSOR,
  /// For detecting subatomic particles
  PARTICLE_DETECTOR,

  // Advanced Optical and Imaging Sensors

  /// Light Detection and Ranging
  LIDAR,
  /// Sound Navigation and Ranging
  SONAR,
  /// General telescopes for astronomical observations
  TELESCOPE,
  /// For spectral analysis
  SPECTROSCOPIC_SENSOR,
  /// For measuring light intensity
  PHOTOMETRIC_SENSOR,
  /// For analyzing polarization of light
  POLARIMETRIC_SENSOR,
  /// For detailed imaging using interference
  INTERFEROMETRIC_SENSOR,
  /// Capturing image data at multiple wavelengths
  MULTISPECTRAL_SENSOR,
  /// Advanced imaging across many spectral bands
  HYPERSPECTRAL_SENSOR,

  // Positioning and Tracking Sensors

  /// For Global Positioning System reception
  GPS_RECEIVER,

  // Communication Devices

  /// Standard radio communication device
  RADIO_COMMUNICATIONS,
  /// Advanced laser communication system
  LASER_COMMUNICATIONS,
  /// Satellite communication system
  SATELLITE_COMMUNICATIONS,

  // Specialized Scientific Instruments

  /// Device for laser-based experiments and measurements
  LASER_INSTRUMENT,
  /// Radio frequency analysis and measurement device
  RF_ANALYZER,
  /// Device for ionospheric research
  IONOSPHERIC_SENSOR,

  // Optical Devices

  /// Device for laser-based imaging
  LASER_IMAGING,
  /// Advanced optical telescope
  OPTICAL_TELESCOPE,
  /// Device for high-resolution optical observations
  HIGH_RESOLUTION_OPTICAL,

  // Radio and Microwave Devices

  RADIO,
  /// Microwave communication device
  MICROWAVE_TRANSMITTER,
  /// Device for radio frequency monitoring
  RF_MONITOR,
  /// High-frequency radio communication device
  HF_RADIO_COMMUNICATIONS,
}

/// Integrated Device Message
table IDM {
  /// Unique identifier for the EMT
  ID: string;
  /// Name of the EMT
  NAME: string;
  /// Mode of the data (real, simulated, synthetic)
  DATA_MODE: DataMode;
  /// Uplink frequency range
  UPLINK: FrequencyRange;
  /// Downlink frequency range
  DOWNLINK: FrequencyRange;
  /// Beacon frequency range
  BEACON: FrequencyRange;
  /// Bands associated with the EMT
  BAND: [Band];
  /// Type of polarization used
  POLARIZATION_TYPE: PolarizationType;
  /// Simple polarization configuration
  SIMPLE_POLARIZATION: SimplePolarization;
  /// Stokes parameters for polarization characterization
  STOKES_PARAMETERS: StokesParameters;
  /// Power required in Watts
  POWER_REQUIRED: double;
  /// Type of power (eg. AC or DC)
  POWER_TYPE: string;
  /// Indicates if the EMT can transmit
  TRANSMIT: bool;
  /// Indicates if the EMT can receive
  RECEIVE: bool;
  /// Type of the sensor
  SENSOR_TYPE: DeviceType;
  /// Source of the data
  SOURCE: string;
  /// Timestamp of the last observation
  LAST_OB_TIME: string;
  /// Lower left elevation limit
  LOWER_LEFT_ELEVATION_LIMIT: double;
  /// Upper left azimuth limit
  UPPER_LEFT_AZIMUTH_LIMIT: double;
  /// Lower right elevation limit
  LOWER_RIGHT_ELEVATION_LIMIT: double;
  /// Lower left azimuth limit
  LOWER_LEFT_AZIMUTH_LIMIT: double;
  /// Upper right elevation limit
  UPPER_RIGHT_ELEVATION_LIMIT: double;
  /// Upper right azimuth limit
  UPPER_RIGHT_AZIMUTH_LIMIT: double;
  /// Lower right azimuth limit
  LOWER_RIGHT_AZIMUTH_LIMIT: double;
  /// Upper left elevation limit
  UPPER_LEFT_ELEVATION_LIMIT: double;
  /// Right geostationary belt limit
  RIGHT_GEO_BELT_LIMIT: double;
