EOO.IDL:
// Hash: a2aff2147d3766df49ebc109b37db2416df55e41405bc96482363392c85730d9
// Version: 1.0.9
// -----------------------------------END_HEADER
include "../RFM/main.fbs";
include "../IDM/main.fbs";

/// Enumeration for data collection methods
enum CollectMethod : byte {
  SIDEREAL,
  RATE_TRACK,
  FIXED_STARE,
  OTHER
}

enum ObservationPosition : byte {
  FENCE,
  FIRST,
  IN,
  LAST,
  SINGLE
}

/// Electro-Optical Observation
table EOO {

  /// Unique identifier of the record.
  ID: string;

  /// Classification marking of the data in IC/CAPCO Portion-marked format.
  CLASSIFICATION: string;

  /// Ob detection time in ISO 8601 UTC (YYYY-MM-DDTHH:MM:SS.ssssssZ), up to microsecond precision.
  OB_TIME: string;

  /// Correlation score of the observation when compared to a known orbit state.
  CORR_QUALITY: float;

  /// Server will auto-populate with SAT_NO if available.
  ID_ON_ORBIT: string;

  /// Unique ID of the sensor. Must have a corresponding sensor record on the server.
  SENSOR_ID: string;

  /// Accepted Collection Method
  COLLECT_METHOD: CollectMethod;

  /// 18SDS satellite number. Only list if correlated against the 18SDS catalog.
  NORAD_CAT_ID: int;

  /// Identifier for the collectRequest message if the collection was in response to tasking.
  TASK_ID: string;

  /// Optional identifier to track a transaction.
  TRANSACTION_ID: string;

  /// The user-defined set ID of a sequence of images.
  IMAGE_SET_ID: string;

  /// The number of images in an image set.
  IMAGE_SET_LENGTH: int;

  /// The sequence ID of an image within an image set.
  SEQUENCE_ID: int;

  /// The position of this observation within a track (FENCE, FIRST, IN, LAST, SINGLE).
  OB_POSITION: ObservationPosition;

  /// Provider maintained ID. May not be consistent with 18SDS SAT_NO.
  ORIG_OBJECT_ID: string;

  /// Sensor ID.
  ORIG_SENSOR_ID: string;

  /// Required if correlation is attempted. Indicates whether correlation succeeded.
  UCT: bool;

  /// Line of sight azimuth angle in degrees and topocentric frame.
  AZIMUTH: float;

  /// One sigma uncertainty in the line of sight azimuth angle, in degrees.
  AZIMUTH_UNC: float;

  /// Sensor line of sight azimuth angle bias in degrees.
  AZIMUTH_BIAS: float;

  /// Rate of change of the line of sight azimuth in degrees per second.
  AZIMUTH_RATE: float;

  /// Line of sight elevation in degrees and topocentric frame.
  ELEVATION: float;

  /// One sigma uncertainty in the line of sight elevation angle, in degrees.
  ELEVATION_UNC: float;

  /// Sensor line of sight elevation bias in degrees.
  ELEVATION_BIAS: float;

  /// Rate of change of the line of sight elevation in degrees per second.
  ELEVATION_RATE: float;

  /// Line of sight range in km. Reported value should include all applicable corrections.
  RANGE: float;

  /// One sigma uncertainty in the line of sight range, in km.
  RANGE_UNC: float;

  /// Sensor line of sight range bias in km.
  RANGE_BIAS: float;

  /// Range rate in km/s. Reported value should include all applicable corrections.
  RANGE_RATE: float;

  /// One sigma uncertainty in the line of sight range rate, in km/sec.
  RANGE_RATE_UNC: float;

  /// Right ascension in degrees. Required metric reporting field for EO observations.
  RA: float;

  /// Line of sight right ascension rate of change, in degrees/sec.
  RA_RATE: float;

  /// One sigma uncertainty in the line of sight right ascension angle, in degrees.
  RA_UNC: float;

  /// Sensor line of sight right ascension bias in degrees.
  RA_BIAS: float;

  /// Declination in degrees. Required metric reporting field for EO observations.
  DECLINATION: float;

  /// Line of sight declination rate of change, in degrees/sec.
  DECLINATION_RATE: float;

  /// One sigma uncertainty in the line of sight declination angle, in degrees.
  DECLINATION_UNC: float;

  /// Sensor line of sight declination angle bias in degrees.
  DECLINATION_BIAS: float;

  /// X-component of the unit vector representing the line-of-sight direction in the observer's reference frame.
  LOSX: float;

  /// Y-component of the unit vector representing the line-of-sight direction in the observer's reference frame.
  LOSY: float;

  /// Z-component of the unit vector representing the line-of-sight direction in the observer's reference frame.
  LOSZ: float;

  /// One sigma uncertainty in the line-of-sight direction vector components.
  LOS_UNC: float;

  /// X-component of the velocity vector along the line of sight, in km/s.
  LOSXVEL: float;

  /// Y-component of the velocity vector along the line of sight, in km/s.
  LOSYVEL: float;

  /// Z-component of the velocity vector along the line of sight, in km/s.
  LOSZVEL: float;

  /// WGS-84 latitude in decimal degrees at the time of the observation.
  SENLAT: float;

  /// WGS-84 longitude in decimal degrees at the time of the observation.
  SENLON: float;

  /// Sensor height in km relative to the WGS-84 ellipsoid at the time of the observation.
  SENALT: float;

  /// Cartesian X position in km at the time of the observation.
  SENX: float;

  /// Cartesian Y position in km at the time of the observation.
  SENY: float;

  /// Cartesian Z position in km at the time of the observation.
  SENZ: float;

  /// Total number of satellites in the field of view.
  FOV_COUNT: int;

  /// Number of uncorrelated satellites in the field of view (JCO).
  FOV_COUNT_UCTS: int;

  /// Image exposure duration in seconds. For observations performed using frame stacking or synthetic tracking methods, 
  /// the exposure duration should be the total integration time. This field is highly recommended / required if the 
  /// observations are going to be used for photometric processing.
  EXP_DURATION: float;

  /// Formula: 2.5 * log_10 (zero_mag_counts / EXP_DURATION).
  ZEROPTD: float;

  /// Net object signature = counts / EXP_DURATION.
  NET_OBJ_SIG: float;

  /// Net object signature uncertainty = counts uncertainty / EXP_DURATION.
  NET_OBJ_SIG_UNC: float;

  /// Measure of observed brightness calibrated against the Gaia G-band.
  MAG: float;

  /// Uncertainty of the observed brightness.
  MAG_UNC: float;

  /// [Definition needed].
  MAG_NORM_RANGE: float;

  /// Computed estimate of the latitude, positive degrees north. It should be computed based on the assumed slant range 
  /// and corresponding viewing geometry. It must NOT be computed from the orbit state.
  GEOLAT: float;

  /// Computed estimate of the longitude as +/- 180 degrees east. It should be computed based on the assumed slant range 
  /// and viewing geometry. It must NOT be computed from the orbit state.
  GEOLON: float;

  /// Computed estimate of satellite altitude in km at the reported location. It must NOT be computed from the orbit state.
  GEOALT: float;

  /// Computed estimate of the slant range in km. It must NOT be computed from the orbit state.
  GEORANGE: float;

  /// Average Sky Background signal, in Magnitudes. Sky Background refers to the incoming light from an apparently 
  /// empty part of the night sky.
  SKY_BKGRND: float;

  /// Primary Extinction Coefficient, in Magnitudes. Primary Extinction is the coefficient applied to the airmass 
  /// to determine how much the observed visual magnitude has been attenuated by the atmosphere. Extinction, in general, 
  /// describes the absorption and scattering of electromagnetic radiation by dust and gas between an emitting astronomical 
  /// object and the observer.
  PRIMARY_EXTINCTION: float;

  /// Primary Extinction Coefficient Uncertainty, in Magnitudes.
  PRIMARY_EXTINCTION_UNC: float;

  /// The angle, in degrees, between the target-to-observer vector and the target-to-sun vector. Recommend using the 
  /// calculation listed in the EOSSA documentation, pg 106 of the EOSSA spec.
  SOLAR_PHASE_ANGLE: float;

  /// The angle, in degrees, between the projections of the target-to-observer vector and the target-to-sun vector 
  /// onto the equatorial plane. The convention used is negative when closing (i.e., before the opposition) 
  /// and positive when opening (after the opposition).
  SOLAR_EQ_PHASE_ANGLE: float;

  /// Angle from the sun to the equatorial plane.
  SOLAR_DEC_ANGLE: float;

  /// Shutter delay in seconds.
  SHUTTER_DELAY: float;

  /// Sensor timing bias in seconds.
  TIMING_BIAS: float;

  /// Optional URI location in the document repository of the raw file parsed by the system to produce this record. 
  RAW_FILE_URI: string;

   /// Intensity of the target for IR observations, in kw/sr/em.
  INTENSITY: float;

  /// Background intensity for IR observations, in kw/sr/um.
  BG_INTENSITY: float;

  /// Optional source-provided and searchable metadata or descriptor of the data.
  DESCRIPTOR: string;

  /// Source of the data.
  SOURCE: string;

  /// Originating system or organization which produced the data, if different from the source.
  /// The origin may be different than the source if the source was a mediating system which forwarded 
  /// the data on behalf of the origin system. If null, the source may be assumed to be the origin.
  ORIGIN: string;

  /// Indicator of whether the data is EXERCISE, REAL, SIMULATED, or TEST.
  DATA_MODE: DataMode;

  /// Time the row was created in the database, auto-populated by the system.
  CREATED_AT: string;

  /// Application user who created the row in the database, auto-populated by the system.
  CREATED_BY: string;

  /// EO observations are assumed to be topocentric J2000 coordinates ('J2000') as defined by the IAU, unless otherwise specified.
  REFERENCE_FRAME: RFM;

  /// The sensor reference frame is assumed to be the International Terrestrial Reference Frame (ITRF), 
  /// unless otherwise specified. (ITRF is equivalent to Earth-Centered Earth-Fixed (ECEF) for this purpose). 
  /// Lat / long / height values should be reported using the WGS-84 ellipsoid, where applicable.
  SEN_REFERENCE_FRAME: RFM;

  /// Boolean indicating that the target object was in umbral eclipse at the time of this observation.
  UMBRA: bool;

  /// Boolean indicating that the target object was in a penumbral eclipse at the time of this observation.
  /// This field is highly recommended if the observations will be used for photometric processing.
  PENUMBRA: bool;

  /// The originating source network on which this record was created, auto-populated by the system.
  ORIG_NETWORK: string;

  /// The source from which this record was received.
  SOURCE_DL: string;

  /// Device Type
  TYPE: DeviceType;

  /// True if measured, false if computed. Required if azimuth is reported.
  AZIMUTH_MEASURED: bool;

  /// True if measured, false if computed. Required if elevation is reported.
  ELEVATION_MEASURED: bool;

  /// True if measured, false if computed. Required if range is reported.
  RANGE_MEASURED: bool;

  /// True if measured, false if computed. Required if range-rate is reported.
  RANGERATE_MEASURED: bool;

  /// True if measured, false if computed. Required if right ascension is reported.
  RA_MEASURED: bool;

  /// True if measured, false if computed. Required if declination is reported.
  DECLINATION_MEASURED: bool;

  /// National Imagery Interpretability Rating Scale (NIIRS). Ranging from 0 (lowest) to 9 (highest).
  NIIRS: float;

  /// Ground sample distance in meters per pixel.
  METERS_PER_PIXEL: float;

  /// Signal-to-noise ratio of the image. Higher values indicate cleaner imagery.
  IMAGE_SNR: float;

  /// Bit depth of the image (e.g., 8, 12, 16).
  IMAGE_BIT_DEPTH: int;

  /// Width of the image in pixels.
  IMAGE_WIDTH: int;

  /// Height of the image in pixels.
  IMAGE_HEIGHT: int;

  /// Compression type used for the image, e.g., "JPEG", "PNG", "RAW", etc.
  IMAGE_COMPRESSION: string;

  /// Compression ratio used (original size / compressed size), if applicable.
  IMAGE_COMPRESSION_RATIO: float;

  /// URI to the processed image used for this observation.
  PROCESSED_IMAGE_URI: string;

  /// Flag indicating whether the image was auto-enhanced (e.g., contrast stretch, denoise).
  IMAGE_AUTO_ENHANCED: bool;

  /// True if the observation was taken with multiple frames stacked into one image.
  MULTI_FRAME_STACKED: bool;

  /// True if synthetic tracking was used to create the image.
  SYNTHETIC_TRACKING_USED: bool;

  /// Sharpness metric of the image based on the Tenengrad method or variance of Laplacian. Higher values indicate sharper images.
  IMAGE_SHARPNESS: float;

  /// Noise level of the image, estimated via pixel intensity variance in background regions.
  IMAGE_NOISE_STDDEV: float;

  /// Contrast metric of the image, such as Michelson contrast or RMS contrast.
  IMAGE_CONTRAST: float;

  /// Dynamic range of the image (max pixel value / min pixel value), indicating tonal spread.
  IMAGE_DYNAMIC_RANGE: float;

  /// Entropy of the image, representing the richness of information content. Higher entropy suggests higher texture detail.
  IMAGE_ENTROPY: float;

  /// Background uniformity metric (e.g., mean gradient in background areas). Lower values indicate more uniform background.
  BACKGROUND_UNIFORMITY: float;

  /// Mean background level, computed from non-object regions in pixel units.
  BACKGROUND_MEAN_LEVEL: float;

  /// Percentage of saturated pixels in the image. Indicates overexposure when high.
  SATURATED_PIXEL_PERCENT: float;

  /// Percentage of dead or zero-value pixels in the image. Indicates sensor defects or underexposure.
  DEAD_PIXEL_PERCENT: float;

  /// Estimated Point Spread Function (PSF) Full Width at Half Maximum (FWHM) in pixels. Indicates image blur or focus.
  PSF_FWHM: float;

  /// Estimated percentage of cloud cover in the image. Derived using cloud detection algorithms such as Fmask or machine learning classifiers.
  CLOUD_COVER_PERCENT: float;

  /// Confidence score of the cloud detection result, from 0 (low confidence) to 1 (high confidence).
  CLOUD_DETECTION_CONFIDENCE: float;

  /// Estimated percentage of the image obscured by haze or atmospheric scattering effects.
  HAZE_PERCENT: float;

  /// Estimated aerosol optical thickness (AOT) at 550 nm, indicating particulate matter in the atmosphere affecting image clarity.
  AEROSOL_OPTICAL_THICKNESS: float;

  /// Estimated water vapor content (e.g., total column precipitable water) at the time of imaging, in mm.
  WATER_VAPOR_CONTENT: float;

  /// Sun elevation angle at the time of image capture, in degrees above the horizon.
  SUN_ELEVATION: float;

  /// Sun azimuth angle at the time of image capture, in degrees from true north.
  SUN_AZIMUTH: float;

  /// View zenith angle (sensor line-of-sight angle from nadir), in degrees.
  VIEW_ZENITH_ANGLE: float;

  /// View azimuth angle (direction of sensor relative to north), in degrees.
  VIEW_AZIMUTH_ANGLE: float;

  /// Off-nadir angle of the sensor at the time of image capture, in degrees.
  OFF_NADIR_ANGLE: float;

  /// Ground coverage width of the image swath in kilometers.
  SWATH_WIDTH_KM: float;

  /// Mean terrain elevation in the image footprint, in meters above sea level.
  MEAN_TERRAIN_ELEVATION: float;

  /// Standard deviation of terrain elevation in the image footprint, in meters.
  TERRAIN_ELEVATION_STDDEV: float;

  /// Percentage of the image affected by shadows, derived via topographic or object shadow detection.
  SHADOW_COVER_PERCENT: float;

  /// Flag indicating whether sunglint is present in the image (true if high reflectance from water surface due to sun geometry).
  SUNGLINT_PRESENT: bool;

  /// Percentage of image affected by sunglint.
  SUNGLINT_PERCENT: float;

  /// Estimated percentage of snow or ice coverage in the image footprint.
  SNOW_ICE_COVER_PERCENT: float;

  /// Total area covered by valid data (non-masked, usable imagery) in square kilometers.
  VALID_DATA_AREA_KM2: float;

}

root_type EOO;
file_identifier "$EOO";

TDM.IDL:
// Hash: 3049e1f1943a27ee1f731bda828d33e815fe37cefe91c98eaca7cbbf088770d1
// Version: 1.0.2
// -----------------------------------END_HEADER
include "../RFM/main.fbs";

// Based on CCSDS 503.0-B-1 standard, November 2007
/// Tracking Data Message
table TDM {

  /// Unique identifier for the observation OBSERVER -  [Specific CCSDS Document]
  OBSERVER_ID: string;

  /// Cartesian X coordinate of the OBSERVER location in chosen reference frame
  OBSERVER_X: double;

  /// Cartesian Y coordinate of the OBSERVER location in chosen reference frame 
  OBSERVER_Y: double;

  /// Cartesian Z coordinate of the OBSERVER location in chosen reference frame 
  OBSERVER_Z: double;

  /// Cartesian X coordinate of the OBSERVER velocity in chosen reference frame
  OBSERVER_VX: double;

  /// Cartesian Y coordinate of the OBSERVER velocity in chosen reference frame 
  OBSERVER_VY: double;

  /// Cartesian Z coordinate of the OBSERVER velocity in chosen reference frame 
  OBSERVER_VZ: double;

  /// Reference frame used for OBSERVER location Cartesian coordinates (e.g., ECEF, ECI)
  OBSERVER_POSITION_REFERENCE_FRAME: RFM;

  /// Reference frame used for obs location Cartesian coordinates (e.g., ECEF, ECI)
  OBS_REFERENCE_FRAME: RFM;

  /// Epoch time or observation time, in ISO 8601 UTC format -  CCSDS 503.0-B-1
  EPOCH: string;

  /// TDM version number -  CCSDS 503.0-B-1, Page D-9
  CCSDS_TDM_VERS: string;

  /// Comments regarding TDM -  various sections, e.g., Page D-9
  COMMENT: [string];

  /// Date of TDM creation -  CCSDS 503.0-B-1, Page D-9
  CREATION_DATE: string;

  /// Originator of the TDM -  CCSDS 503.0-B-1, Page D-9
  ORIGINATOR: string;

  /// Start of metadata section -  CCSDS 503.0-B-1, Page D-9
  META_START: string;

  /// Time system used -  CCSDS 503.0-B-1, Page D-9
  TIME_SYSTEM: string;

  /// Start time of the data -  CCSDS 503.0-B-1, Page D-9
  START_TIME: string;

  /// Stop time of the data -  CCSDS 503.0-B-1, Page D-9
  STOP_TIME: string;

  /// First participant in the TDM -  CCSDS 503.0-B-1, Page D-9
  PARTICIPANT_1: string;

  /// Second participant in the TDM -  CCSDS 503.0-B-1, Page D-9
  PARTICIPANT_2: string;

  /// Third participant in the TDM (if applicable) -  CCSDS 503.0-B-1, Page D-9
  PARTICIPANT_3: string;

  /// Fourth participant in the TDM (if applicable) -  CCSDS 503.0-B-1, Page D-9
  PARTICIPANT_4: string;

  /// Fifth participant in the TDM (if applicable) -  CCSDS 503.0-B-1, Page D-9, max participants
  PARTICIPANT_5: string;

  /// Mode of TDM -  CCSDS 503.0-B-1, Page D-9
  MODE: string;

  /// First path in TDM -  CCSDS 503.0-B-1, Page D-9
  PATH_1: ushort;

  /// Second path in TDM (if applicable) -  CCSDS 503.0-B-1, Page D-9
  PATH_2: ushort;

  /// Transmit band -  CCSDS 503.0-B-1, Page D-9
  TRANSMIT_BAND: string;

  /// Receive band -  CCSDS 503.0-B-1, Page D-9
  RECEIVE_BAND: string;

  /// Integration interval -  CCSDS 503.0-B-1, Page D-9
  INTEGRATION_INTERVAL: float;

  /// Integration reference -  CCSDS 503.0-B-1, Page D-9
  INTEGRATION_REF: string;

  /// Receive delay for second participant -  CCSDS 503.0-B-1, Page D-9
  RECEIVE_DELAY_2: double;

  /// Receive delay for third participant -  CCSDS 503.0-B-1, Page D-9
  RECEIVE_DELAY_3: double;

  /// Data quality -  CCSDS 503.0-B-1, Page D-9
  DATA_QUALITY: string;

  /// End of metadata section -  CCSDS 503.0-B-1, Page D-9
  META_STOP: string;

  /// Start of data section -  CCSDS 503.0-B-1, Page D-9
  DATA_START: string;

  /// Transmit frequency for first participant -  CCSDS 503.0-B-1, Page D-9
  TRANSMIT_FREQ_1: double;

  /// Receive frequency -  CCSDS 503.0-B-1, Page D-9
  RECEIVE_FREQ: [double];

  /// End of data section -  CCSDS 503.0-B-1, Page D-9
  DATA_STOP: string;

  /// Additional properties as required by the specific application of the TDM...
  /// Reference for time tagging -  CCSDS 503.0-B-1, Page D-10
  TIMETAG_REF: string;

  /// Type of angle data -  CCSDS 503.0-B-1, Page D-12
  /// Can be AZEL, RADEC, XEYN, XSYE, or another value with provided ICD
  ANGLE_TYPE: string;

  /// First angle value -  CCSDS 503.0-B-1, Page D-12
  ANGLE_1: [float];

  /// Second angle value -  CCSDS 503.0-B-1, Page D-12
  ANGLE_2: [float];

  /// Uncertainty of first angle -  CCSDS 503.0-B-1
  ANGLE_UNCERTAINTY_1: float;

  /// Uncertainty of second angle -  CCSDS 503.0-B-1
  ANGLE_UNCERTAINTY_2: float;

  /// Rate of change of range -  CCSDS 503.0-B-1
  RANGE_RATE: double;

  /// Uncertainty in range -  CCSDS 503.0-B-1
  RANGE_UNCERTAINTY: double;

  /// Mode of range data -  CCSDS 503.0-B-1, Page D-10
  RANGE_MODE: string;

  /// Modulus value for range data -  CCSDS 503.0-B-1, Page D-10
  RANGE_MODULUS: double;

  /// First correction angle -  CCSDS 503.0-B-1, Page D-12
  CORRECTION_ANGLE_1: float;

  /// Second correction angle -  CCSDS 503.0-B-1, Page D-12
  CORRECTION_ANGLE_2: float;

  /// Indicator of corrections applied -  CCSDS 503.0-B-1, Page D-12
  CORRECTIONS_APPLIED: string;

  /// Dry component of tropospheric delay -  CCSDS 503.0-B-1, Page D-14
  TROPO_DRY: [double];

  /// Wet component of tropospheric delay -  CCSDS 503.0-B-1, Page D-14
  TROPO_WET: [double];

  /// Slant total electron content -  CCSDS 503.0-B-1, Page D-13
  STEC: [double];

  /// Atmospheric pressure -  CCSDS 503.0-B-1, Page D-14
  PRESSURE: [double];

  /// Relative humidity -  CCSDS 503.0-B-1, Page D-14
  RHUMIDITY: [double];

  /// Ambient temperature -  CCSDS 503.0-B-1, Page D-14
  TEMPERATURE: [double];

  /// Clock bias values -  CCSDS 503.0-B-1, Page D-15
  CLOCK_BIAS: [double];

  /// Clock drift values -  CCSDS 503.0-B-1, Page D-15
  CLOCK_DRIFT: [double];
}

root_type TDM;
file_identifier "$TDM";

OCM.IDL:
// Hash: 333db757e4fde574bac5e8bd7c4fd5c3f3b42c8d17737e2aaa8b8233f06c403c
// Version: 1.0.4
// -----------------------------------END_HEADER
include "../ATM/main.fbs";

// Orbit Comprehensive Message (OCM)
// Description
// The Orbit Comprehensive Message (OCM) is a standardized format used for exchanging detailed
// orbit data between participants. It includes state vectors, physical properties, maneuver information,
// perturbation parameters, covariance data, orbit determination details, and user-defined parameters.
// The OCM is designed to provide a comprehensive set of orbit-related information to support mission
// operations, analysis, and planning activities.
// CCSDS Reference documentation: 502.0-B-3 (April 2023).

table Header {
  /// Format version in the form of 'x.y', where 'y' is incremented for minor changes, and 'x' for major changes.
  CCSDS_OCM_VERS: string;
  /// Comments (a contiguous set of one or more comment lines may be provided immediately after the version number).
  COMMENT: [string];
  /// User-defined free-text message classification/caveats of this OCM.
  CLASSIFICATION: string;
  /// File creation date/time in UTC.
  CREATION_DATE: string;
  /// Creating agency or operator.
  ORIGINATOR: string;
  /// Free-text field containing an ID that uniquely identifies a message from this originator.
  MESSAGE_ID: string;
}

table Metadata {
  /// Comments in the Metadata section.
  COMMENT: [string];
  /// Free-text field containing the name of the object.
  OBJECT_NAME: string;
  /// International designator for the object as assigned by COSPAR.
  INTERNATIONAL_DESIGNATOR: string;
  /// Satellite catalog source from which 'OBJECT_DESIGNATOR' was obtained.
  CATALOG_NAME: string;
  /// Unique satellite identification designator from the catalog.
  OBJECT_DESIGNATOR: string;
  /// Alternate names of this space object.
  ALTERNATE_NAMES: [string];
  /// Originator or programmatic Point-of-Contact (PoC) for OCM.
  ORIGINATOR_POC: string;
  /// Contact position of the originator PoC.
  ORIGINATOR_POSITION: string;
  /// Originator PoC phone number.
  ORIGINATOR_PHONE: string;
  /// Originator PoC email address.
  ORIGINATOR_EMAIL: string;
  /// Originator's physical address information.
  ORIGINATOR_ADDRESS: string;
  /// Creating agency or operator for technical contact.
  TECH_ORG: string;
  /// Technical Point-of-Contact (PoC) for OCM.
  TECH_POC: string;
  /// Contact position of the technical PoC.
  TECH_POSITION: string;
  /// Technical PoC phone number.
  TECH_PHONE: string;
  /// Technical PoC email address.
  TECH_EMAIL: string;
  /// Technical PoC physical address information.
  TECH_ADDRESS: string;
  /// ID that uniquely identifies the previous message from this originator.
  PREVIOUS_MESSAGE_ID: string;
  /// ID that uniquely identifies the next message from this originator.
  NEXT_MESSAGE_ID: string;
  /// Unique identifier of Attitude Data Messages linked to this OCM.
  ADM_MSG_LINK: string;
  /// Unique identifier of Conjunction Data Messages linked to this OCM.
  CDM_MSG_LINK: string;
  /// Unique identifier of Pointing Request Messages linked to this OCM.
  PRM_MSG_LINK: string;
  /// Unique identifier of Reentry Data Messages linked to this OCM.
  RDM_MSG_LINK: string;
  /// List of Tracking Data Messages upon which this orbit determination is based.
  TDM_MSG_LINK: [string];
  /// Operator of the space object.
  OPERATOR: string;
  /// Owner of the space object.
  OWNER: string;
  /// Country where the space object owner is based.
  COUNTRY: string;
  /// Name of the constellation to which this space object belongs.
  CONSTELLATION: string;
  /// Specification of the type of object (e.g., PAYLOAD, ROCKET BODY, DEBRIS).
  OBJECT_TYPE: string;
  /// Time system for all absolute time stamps in this OCM, including EPOCH_TZERO.
  TIME_SYSTEM: string;
  /// Default epoch to which all relative times are referenced in data blocks.
  EPOCH_TZERO: string;
  /// Specification of the operational status of the space object.
  OPS_STATUS: string;
  /// Specification of the type of orbit (e.g., GEO, LEO).
  ORBIT_CATEGORY: string;
  /// List of elements of information data blocks included in this message.
  OCM_DATA_ELEMENTS: [string];
  /// Number of spacecraft clock counts at EPOCH_TZERO (used if TIME_SYSTEM is SCLK).
  SCLK_OFFSET_AT_EPOCH: double;
  /// Number of clock seconds occurring during one SI second (used if TIME_SYSTEM is SCLK).
  SCLK_SEC_PER_SI_SEC: double;
  /// Creation epoch of the previous message from this originator.
  PREVIOUS_MESSAGE_EPOCH: string;
  /// Anticipated (or actual) epoch of the next message from this originator.
  NEXT_MESSAGE_EPOCH: string;
  /// Time of the earliest data contained in the OCM.
  START_TIME: string;
  /// Time of the latest data contained in the OCM.
  STOP_TIME: string;
  /// Span of time that the OCM covers, measured in days.
  TIME_SPAN: double;
  /// Difference (TAI - UTC) in seconds at EPOCH_TZERO.
  TAIMUTC_AT_TZERO: double;
  /// Epoch of next leap second.
  NEXT_LEAP_EPOCH: string;
  /// Difference (TAI - UTC) in seconds at NEXT_LEAP_EPOCH.
  NEXT_LEAP_TAIMUTC: double;
  /// Difference (UT1 - UTC) in seconds at EPOCH_TZERO.
  UT1MUTC_AT_TZERO: double;
  /// Source and version of Earth Orientation Parameters used.
  EOP_SOURCE: string;
  /// Method used to select or interpolate sequential EOP data.
  INTERP_METHOD_EOP: string;
  /// Source and version of celestial body ephemeris data used.
  CELESTIAL_SOURCE: string;
}

table StateVector {
  /// Epoch of the state vector.
  EPOCH: string;
  /// X component of position in the specified reference frame.
  X: double;
  /// Y component of position.
  Y: double;
  /// Z component of position.
  Z: double;
  /// X component of velocity.
