include "../STF/main.fbs";

/// Access Control Grant - Permission to access purchased data
table ACL {
  /// Unique identifier for this grant
  GRANT_ID: string (required);
  /// ID of the listing this grant applies to
  LISTING_ID: string (required);
  /// Peer ID of the buyer/grantee
  BUYER_PEER_ID: string (required);
  /// Buyer's encryption public key for encrypted delivery
  BUYER_ENCRYPTION_PUBKEY: [ubyte];

  /// Type of access granted
  ACCESS_TYPE: accessType;
  /// Name of the pricing tier purchased
  TIER_NAME: string;

  /// Unix timestamp when access was granted
  GRANTED_AT: uint64;
  /// Unix timestamp when access expires (0 = never expires)
  EXPIRES_AT: uint64;

  /// Transaction hash or reference proving payment
  PAYMENT_TX_HASH: string;
  /// Payment method used
  PAYMENT_METHOD: paymentMethod;

  /// Ed25519 signature from provider
  PROVIDER_SIGNATURE: [ubyte];
}

root_type ACL;
file_identifier "$ACL";

PNM.IDL:
// Hash: 0fd38107c4f49f612457f9e117a96e00c2d1ec719ec1ee8315d73a50357545c4
// Version: 1.0.1
// -----------------------------------END_HEADER
/// Publish Notification Message
table PNM {
  /// Multiformat Address
  /// https://multiformats.io/multiaddr/
  /// A universal address format for representing multiple network protocols. Examples include:
  /// - /ip4/192.168.1.1/tcp/80 for an IPv4 address with TCP protocol
  /// - /ip6zone/x/ip6/::1 for an IPv6 address with a zone
  /// - /dns4/example.com for a domain name resolvable only to IPv4 addresses
  /// - /ipfs/bafybeiccfclkdtucu6y4yc5cpr6y3yuinr67svmii46v5cfcrkp47ihehy/README.txt -IPFS address w/CID and path to `README.txt`.
  MULTIFORMAT_ADDRESS: string;

  /// Publish Time OF THE Publish Notification Message
  PUBLISH_TIMESTAMP: string;

  /// Concatenated Content Identifier (CID)
  /// This field is a unique ID for distributed systems (CID).
  /// The CID provides a unique identifier within distributed systems, as detailed at https://github.com/multiformats/cid. 
  CID: string;

  /// File ID
  /// This field is the Name
  FILE_NAME: string;

  /// File ID
  /// This field is the file ID / Standard Type
  FILE_ID: string;

  /// Digital Signature of the CID
  /// This is the digital signature of the CID, signed using the specified cryptographic method.
  SIGNATURE: string;

  /// Timestamp Signature
  /// Digital signature of the publish timestamp, using the specified cryptographic method for timestamp verification.
  TIMESTAMP_SIGNATURE: string;

  /// Type of Cryptographic Signature Used
  /// Specifies the type of cryptographic signature used for the SIGNATURE field, indicating the specific blockchain technology, such as Ethereum or BTC.
  SIGNATURE_TYPE: string;

  /// Type of Cryptographic Signature Used for Timestamp
  /// Specifies the type of cryptographic signature used for the TIMESTAMP_SIGNATURE field, indicating the specific blockchain technology, such as Ethereum or BTC.
  TIMESTAMP_SIGNATURE_TYPE: string;
}

root_type PNM;
file_identifier "$PNM";

STF.IDL:
// Hash: dbdb17407c2ee4d78f5e9ec77631c27813246acad984953f954fb35519a98780
// Version: 1.0.1
// -----------------------------------END_HEADER
/// Access type for data listings
enum accessType : byte {
  /// One-time purchase
  OneTime,
  /// Recurring subscription
  Subscription,
  /// Real-time streaming access
  Streaming,
  /// Query-based access
  Query
}

/// Payment method accepted
enum paymentMethod : byte {
  /// Ethereum cryptocurrency
  Crypto_ETH,
  /// Solana cryptocurrency
  Crypto_SOL,
  /// Bitcoin cryptocurrency
  Crypto_BTC,
  /// Internal SDN credits system
  SDN_Credits,
  /// Fiat payment via Stripe
  Fiat_Stripe,
  /// Free/open data
  Free
}

/// Spatial coverage definition
table SpatialCoverage {
  /// Type of coverage: "global", "region", "object_list"
  TYPE: string;
  /// Regions covered, e.g., ["LEO", "GEO", "MEO"]
  REGIONS: [string];
  /// Specific NORAD IDs or catalog numbers
  OBJECT_IDS: [string];
}

/// Temporal coverage definition
table TemporalCoverage {
  /// Start epoch in ISO 8601 format
  START_EPOCH: string;
  /// End epoch in ISO 8601 format
  END_EPOCH: string;
  /// Update frequency: "realtime", "hourly", "daily"
  UPDATE_FREQUENCY: string;
  /// Days of historical data available
  HISTORICAL_DEPTH: uint32;
}

/// Data coverage combining spatial and temporal
table DataCoverage {
  /// Spatial coverage definition
  SPATIAL: SpatialCoverage;
  /// Temporal coverage definition
  TEMPORAL: TemporalCoverage;
}

/// Pricing tier for a listing
table PricingTier {
  /// Tier name, e.g., "Basic", "Pro", "Enterprise"
  NAME: string;
  /// Price in smallest unit (cents, satoshis, etc.)
  PRICE_AMOUNT: uint64;
  /// Currency code: "USD", "ETH", "SOL", "SDN_CREDITS"
  PRICE_CURRENCY: string;
  /// Duration in days (0 = one-time purchase)
  DURATION_DAYS: uint32;
  /// Rate limit in requests per hour
  RATE_LIMIT: uint32;
  /// List of features included in this tier
  FEATURES: [string];
}

/// Storefront Listing - Data marketplace listing
table STF {
  /// Unique identifier for the listing
  LISTING_ID: string (required);
  /// Peer ID of the data provider
  PROVIDER_PEER_ID: string (required);
  /// IPFS CID of provider's EPM (Entity Profile Message)
  PROVIDER_EPM_CID: string;

  /// Title of the data listing
  TITLE: string (required);
  /// Detailed description of the data offering
  DESCRIPTION: string;
  /// SDS data types offered, e.g., ["OMM", "CDM", "TLE"]
  DATA_TYPES: [string];
  /// Coverage information (spatial and temporal)
  COVERAGE: DataCoverage;
  /// IPFS CID of sample data
  SAMPLE_CID: string;

  /// Type of access offered
  ACCESS_TYPE: accessType;
  /// Whether encryption is required for data delivery
  ENCRYPTION_REQUIRED: bool;

  /// Available pricing tiers
  PRICING: [PricingTier];
  /// Payment methods accepted
  ACCEPTED_PAYMENTS: [paymentMethod];

  /// Unix timestamp when listing was created
  CREATED_AT: uint64;
  /// Unix timestamp when listing was last updated
  UPDATED_AT: uint64;
  /// Whether the listing is currently active
  ACTIVE: bool;

  /// Ed25519 signature from provider
  SIGNATURE: [ubyte];
}

root_type STF;
file_identifier "$STF";
